mapper.sql:
SELECT a.course_id,
       if(b.course_code_alias is null,a.course_code,b.course_code_alias) as 'courseCode',
       if(b.course_name_alias is null,a.course_name,b.course_name_alias) as 'courseName',
       a.comments,a.deleted,a.tenant_id,a.course_picture,a.sort_number,a.create_time,a.update_time
FROM t_course a
LEFT JOIN t_course_alias b
    ON  b.tenant_id = #{param.tenantIdAlias}
    AND a.course_id = b.course_id
    AND b.deleted = 0


        AND a.course_id = #{param.courseId}


        AND a.course_id in

            #{courseId}



        AND a.course_code LIKE CONCAT('%', #{param.courseCode}, '%')


        AND  a.course_code IN

            #{courseCode}




        AND a.course_name LIKE CONCAT('%', #{param.courseName}, '%')


        AND a.course_picture LIKE CONCAT('%', #{param.coursePicture}, '%')


        AND a.sort_number = #{param.sortNumber}


        AND a.comments LIKE CONCAT('%', #{param.comments}, '%')


        AND a.deleted = #{param.deleted}


        AND a.deleted = 0


        AND a.create_time >= #{param.createTimeStart}


        AND a.create_time <= #{param.createTimeEnd}


        AND a.tenant_id = #{param.tenantId}



        AND a.tenant_id in

            #{tenantId}




        AND (
        a.course_code LIKE CONCAT('%', #{param.keywords}, '%')
        OR a.course_name LIKE CONCAT('%', #{param.keywords}, '%')
        OR b.course_code_alias LIKE CONCAT('%', #{param.keywords}, '%')
        OR b.course_name_alias LIKE CONCAT('%', #{param.keywords}, '%')
        )
