mapper.sql:
SELECT
    a.*,
    b.major_year
FROM
    t_major_course a join t_major b on a.major_id = b.major_id and b.deleted=0


        AND a.id = #{param.id}


        AND b.major_year =  #{param.majorYear}


        AND ((a.semester = ((#{param.academicYear} - b.major_year)*2+1)  or a.semester = ((#{param.academicYear} - b.major_year)*2)+2))


        AND a.semester%2 = #{param.academicSemester}


        AND a.semester = #{param.semester}


        AND a.major_id = #{param.majorId}


        AND a.course_id = #{param.courseId}


        AND a.deleted = #{param.deleted}


        AND a.deleted = 0


        AND a.create_time >= #{param.createTimeStart}


        AND a.create_time <= #{param.createTimeEnd}

mapper.select:
SELECT major_id as majorId,max(tmc.semester) as lastSemester from t_major_course tmc WHERE tmc.deleted = 0 and tmc.major_id in
#{majorId}
group by major_id
