mapper.sql:
SELECT a.*
FROM t_major a


        AND a.major_id = #{param.majorId}


        AND a.major_year = #{param.majorYear}


        AND (#{param.academicYear} >= a.major_year and (a.major_year +CEILING(a.major_length)-1)>=#{param.academicYear})


        AND a.major_code LIKE CONCAT('%', #{param.majorCode}, '%')


        AND a.major_name LIKE CONCAT('%', #{param.majorName}, '%')


        AND a.major_picture LIKE CONCAT('%', #{param.majorPicture}, '%')


        AND a.major_type = #{param.majorType}


        AND a.major_gradation = #{param.majorGradation}


        AND a.major_forms = #{param.majorForms}


        AND a.major_length = #{param.majorLength}


        AND a.sort_number = #{param.sortNumber}


        AND a.comments LIKE CONCAT('%', #{param.comments}, '%')


        AND a.deleted = #{param.deleted}


        AND a.deleted = 0


        AND a.create_time >= #{param.createTimeStart}


        AND a.create_time <= #{param.createTimeEnd}


        AND (
        a.major_year LIKE CONCAT('%', #{param.keywords}, '%')
        OR a.major_code LIKE CONCAT('%', #{param.keywords}, '%')
        OR a.major_name LIKE CONCAT('%', #{param.keywords}, '%')
        OR a.major_type LIKE CONCAT('%', #{param.keywords}, '%')
        OR a.major_gradation LIKE CONCAT('%', #{param.keywords}, '%')
        OR a.major_forms LIKE CONCAT('%', #{param.keywords}, '%')
        )
