mapper.sql:
SELECT a.*,
       c.semester,
       b.org_id,
       b.user_number,
       m.major_year
FROM t_user_course a
join t_user_major b on a.user_id=b.user_id and a.major_id= b.major_id and b.deleted=0
join t_major m on m.major_id = a.major_id and m.deleted=0
join t_major_course c on c.major_id = a.major_id and c.course_id = a.course_id and c.deleted=0


        AND a.id = #{param.id}


        AND a.user_id = #{param.userId}


        AND a.major_id = #{param.majorId}


        AND m.major_year =  #{param.majorYear}


        AND ((c.semester = ((#{param.academicYear} - m.major_year)*2+1)  or c.semester = ((#{param.academicYear} - m.major_year)*2)+2))


        AND c.semester%2 = #{param.academicSemester}


        AND c.semester = #{param.semester}


        AND a.course_id = #{param.courseId}


        AND a.deleted = #{param.deleted}


        AND a.deleted = 0


        AND b.org_id = #{param.orgId}



        AND b.org_id in

            #{orgId}



        AND a.create_time >= #{param.createTimeStart}


        AND a.create_time <= #{param.createTimeEnd}


        AND a.major_id in

            #{majorId}



        AND b.id in

            #{userMajorId}




        AND a.user_id in

            #{userId}



        AND a.course_id in

            #{courseId}



        AND m.major_year in

            #{majorYear}



        AND (a.total_score is null or  #{param.totalScore} > a.total_score)
