mapper.sql:
SELECT
a.id,
a.paper_id,
a.status ,
a.start_time,
a.end_time,
a.score,
a.update_time,
u.user_id,
u.org_id,
d.course_id,
b.major_id,
d.semester,
e.major_course_exam_id
FROM
t_user_major_course_exam a
left join t_major_course_exam e on a.major_course_exam_id = e.major_course_exam_id and e.deleted=0
left join t_major_course d on e.major_course_id = d.id and d.deleted=0
left join t_major b on  d.major_id = b.major_id and b.deleted=0
left join t_user_major u on u.user_id = a.user_id and b.major_id = u.major_id and u.deleted=0


        AND a.id = #{param.id}


        AND a.id in

            #{id}



        AND b.major_year =  #{param.majorYear}


        AND ((d.semester = ((#{param.academicYear} - b.major_year)*2+1)  or d.semester = ((#{param.academicYear} - b.major_year)*2)+2))


        AND d.semester%2 = #{param.academicSemester}


        AND d.semester = #{param.semester}


        AND b.major_id = #{param.majorId}


        AND d.course_id = #{param.courseId}


        AND a.status = #{param.status}


        AND a.user_id = #{param.userId}


        AND a.deleted = #{param.deleted}


        AND a.deleted = 0


        AND u.org_id = #{param.orgId}



        AND u.org_id in

            #{orgId}



        AND a.create_time >= #{param.createTimeStart}


        AND a.create_time <= #{param.createTimeEnd}

SELECT
3 as 'status',
e.update_time,
u.user_id,
u.org_id,
d.course_id,
b.major_id,
d.semester,
e.major_course_exam_id
FROM
t_major_course_exam e
left join t_major_course d on e.major_course_id = d.id and d.deleted=0
left join t_major b on  d.major_id = b.major_id and b.deleted=0
left join t_user_major u on b.major_id = u.major_id and u.deleted=0

    and now() > e.start_time
    and e.end_time > now()
    and (select count(1) from t_user_major_course_exam a where a.major_course_exam_id = e.major_course_exam_id and a.user_id =u.user_id  and a.deleted=0) = 0
    and (select count(1) from t_user_course c where c.user_id =u.user_id and d.major_id = c.major_id and d.course_id = c.course_id and c.deleted=0 and (c.total_score is null or 60 > c.total_score)) =1

        AND b.major_year =  #{param.majorYear}


        AND ((d.semester = ((#{param.academicYear} - b.major_year)*2+1)  or d.semester = ((#{param.academicYear} - b.major_year)*2)+2))


        AND d.semester%2 = #{param.academicSemester}


        AND d.semester = #{param.semester}


        AND b.major_id = #{param.majorId}


        AND d.course_id = #{param.courseId}


        AND u.user_id = #{param.userId}


        AND e.deleted = #{param.deleted}


        AND e.deleted = 0


        AND u.org_id = #{param.orgId}



        AND u.org_id in

            #{orgId}



        AND e.create_time >= #{param.createTimeStart}


        AND e.create_time <= #{param.createTimeEnd}
