mapper.sql:
SELECT a.*,
b.dict_code,
b.dict_name,
pb.dict_name as parent_dict_name,
pb.dict_code as parent_dict_code,
p.dict_data_code as parent_dict_data_code,
p.dict_data_name as parent_dict_data_name
FROM sys_dictionary_data a
LEFT JOIN sys_dictionary b ON a.dict_id = b.dict_id
LEFT JOIN sys_dictionary_data p ON a.parent_id = p.dict_data_id
LEFT JOIN sys_dictionary pb ON pb.dict_id = p.dict_id

    AND a.deleted = 0

        AND a.dict_data_id = #{param.dictDataId}


        AND a.parent_id = #{param.parentId}


        AND a.dict_id = #{param.dictId}


        AND a.dict_data_code LIKE CONCAT('%', #{param.dictDataCode}, '%')


        AND a.dict_data_name LIKE CONCAT('%', #{param.dictDataName}, '%')


        AND a.comments LIKE CONCAT('%', #{param.comments}, '%')


        AND a.create_time >= #{param.createTimeStart}


        AND a.create_time <= #{param.createTimeEnd}


        AND b.dict_code = #{param.dictCode}


        AND b.dict_name = #{param.dictName}


        AND b.dict_name = #{param.dictName}


        AND p.dict_data_code  = #{param.parentDictDataCode}


        AND (
        a.dict_data_code LIKE CONCAT('%', #{param.keywords}, '%')
        OR a.dict_data_name LIKE CONCAT('%', #{param.keywords}, '%')
        OR b.dict_code LIKE CONCAT('%', #{param.keywords}, '%')
        OR b.dict_name LIKE CONCAT('%', #{param.keywords}, '%')
        )
