mapper.select:
SELECT a.meta,a.menu_type,a.component,a.menu_id,a.parent_id,a.title,a.path,a.menu_type,a.sort_number,a.icon,a.hide,a.authority
from sys_menu a

    AND a.menu_id IN
    (
    SELECT menu_id FROM sys_role_menu WHERE role_id IN
       (
        SELECT ta.role_id FROM sys_user_role ta,sys_role tb
        WHERE ta.role_id = tb.role_id and ta.user_id = #{userId} AND tb.deleted = 0
        )
    )

        AND a.menu_type = #{menuType}


        AND a.app_type = #{appType}

    AND a.deleted = 0

SELECT a.meta,a.menu_type,a.component,a.menu_id,a.parent_id,a.title,a.path,a.menu_type,a.sort_number,a.icon,a.hide,a.authority,b.role_id
FROM sys_role_menu b join sys_menu a on a.menu_id = b.menu_id

    AND b.role_id IN

        #{roleId}


        AND a.menu_type = #{menuType}


        AND a.app_type = #{appType}

    AND a.deleted = 0
