mapper.select:
select m.*,if(rea.id,1,0) as has_read from ( SELECT distinct b.announcement_id,b.title,b.top_timestamp,b.create_time
,b.content_text,b.announcement_picture,b.announcement_type
FROM sys_announcement_receive a join sys_announcement b on b.deleted=0 and a.announcement_id = b.announcement_id
a.deleted = 0
AND b.title LIKE CONCAT('%', #{param.title}, '%')
AND b.announcement_type = #{param.announcementType}
and ( (a.receive_type='1' and (a.add_newly = 1 or( a.add_newly = 0 and a.create_time > #{param.userTenantCreateTime}))) or (a.receive_type='4' and a.receive_id = #{param.userId} ) or (a.receive_type ='2' and a.receive_id in(SELECT o.org_id from sys_user_org o WHERE o.user_id = #{param.userId} and (a.add_newly =1 or (a.add_newly=0 and a.create_time >= o.create_time)))) or (a.receive_type ='3' and a.receive_id in(SELECT ro.role_id from sys_user_role ro WHERE ro.user_id = #{param.userId} and (a.add_newly =1 or (a.add_newly=0 and a.create_time >= ro.create_time)))) or (a.receive_type ='5' and a.receive_id in(SELECT op.group_id from sys_user_group op WHERE op.user_id = #{param.userId} and (a.add_newly =1 or (a.add_newly=0 and a.create_time >= op.create_time)))) )
) m left join sys_announcement_read rea on rea.deleted=0 and rea.user_id = #{param.userId} and m.announcement_id = rea.announcement_id
where if(rea.id,1,0) = #{param.hasRead}
order by m.top_timestamp desc,m.create_time desc
