mapper.select:
select m.*,if(rea.id,1,0) as has_read from ( SELECT distinct b.system_announcement_id,b.title,b.top_timestamp,b.create_time
,b.content_text,b.system_announcement_picture,b.system_announcement_type
FROM sys_system_announcement_receive a join sys_system_announcement b on b.deleted=0 and a.system_announcement_id = b.system_announcement_id
a.deleted = 0
AND b.title LIKE CONCAT('%', #{param.title}, '%')
AND b.system_announcement_type = #{param.systemAnnouncementType}
and ( (a.receive_type='1' and (a.add_newly = 1 or( a.add_newly = 0 and a.create_time > #{param.userCreateTime}))) or(a.receive_type='4' and a.receive_id = #{param.userId} ) or(a.receive_type='3' AND a.receive_id in (SELECT distinct sr.system_default from sys_user_role ro join sys_role sr on ro.role_id = sr.role_id WHERE ro.user_id = #{param.userId} and (a.add_newly =1 or (a.add_newly=0 and a.create_time >= ro.create_time)))) or(a.receive_type ='2' and a.receive_id in(SELECT distinct t.tenant_id from sys_user_tenant t WHERE t.user_id = #{param.userId} and (a.add_newly =1 or (a.add_newly=0 and a.create_time >= t.create_time)))) )
) m left join sys_system_announcement_read rea on rea.deleted=0 and rea.user_id = #{param.userId} and m.system_announcement_id = rea.system_announcement_id
where if(rea.id,1,0) = #{param.hasRead}
order by m.top_timestamp desc,m.create_time desc
