mapper.sql:
SELECT a.tenant_id,a.parent_id,a.tenant_name,a.tenant_type,a.tenant_full_name,a.tenant_code,a.tenant_logo,a.update_time,a.create_time
FROM sys_tenant a


        AND a.tenant_id = #{param.tenantId}


        AND a.tenant_name LIKE CONCAT('%', #{param.tenantName}, '%')


        AND a.tenant_type = #{param.tenantType}


        AND a.comments LIKE CONCAT('%', #{param.comments}, '%')


        AND a.tenant_full_name LIKE CONCAT('%', #{param.tenantFullName}, '%')


        AND a.tenant_code LIKE CONCAT('%', #{param.tenantCode}, '%')


        AND a.deleted = #{param.deleted}


        AND a.deleted = 0


        AND a.create_time >= #{param.createTimeStart}


        AND a.create_time <= #{param.createTimeEnd}
