mapper.sql:
SELECT a.*,t.tenant_id,t.user_type,t.create_time as userTenantCreateTime FROM sys_user_tenant t join sys_user a on a.user_id = t.user_id
AND a.user_id = #{param.userId}
AND a.username LIKE CONCAT('%', #{param.username}, '%')
AND a.realname LIKE CONCAT('%', #{param.realname}, '%')
AND a.sex = #{param.sex}
AND a.phone LIKE CONCAT('%', #{param.phone}, '%')
AND a.email LIKE CONCAT('%', #{param.email}, '%')
AND a.id_card LIKE CONCAT('%', #{param.idCard}, '%')
AND a.birthday LIKE CONCAT('%', #{param.birthday}, '%')
AND a.`status` = #{param.status}
AND a.create_time >= #{param.createTimeStart}
AND a.create_time <= #{param.createTimeEnd}
AND a.deleted = #{param.deleted}
AND a.deleted = 0
AND a.user_id IN (SELECT user_id FROM sys_user_role WHERE role_id=#{param.roleId})
AND a.user_id IN (SELECT user_id FROM sys_user_org WHERE org_id=#{param.orgId})
AND a.user_id IN (SELECT user_id FROM sys_user_group WHERE group_id=#{param.groupId})
AND a.user_id IN (SELECT ur.user_id FROM sys_role r join sys_user_role ur on r.deleted=0 and r.role_id = ur.role_id and r.role_code=#{param.roleCode})
AND a.user_id IN (SELECT ur.user_id FROM sys_org r join sys_user_org ur on r.deleted=0 and r.org_id = ur.org_id and r.org_code=#{param.orgCode})
AND a.user_id IN (SELECT ur.user_id FROM sys_group r join sys_user_group ur on r.deleted=0 and r.group_id = ur.group_id and r.group_code=#{param.groupCode})
AND a.user_id IN ( SELECT user_id FROM sys_user_org WHERE org_id in
#{orgId}
)
AND ( a.username LIKE CONCAT('%', #{param.keywords}, '%') OR a.realname LIKE CONCAT('%', #{param.keywords}, '%') OR a.id_card LIKE CONCAT('%', #{param.keywords}, '%') OR a.phone LIKE CONCAT('%', #{param.keywords}, '%') )

mapper.select:
SELECT a.user_id,a.current_tenant_id,a.password,a.`status` FROM sys_user a

    AND a.deleted = 0
    AND a.username = #{username}
